def longestPalindrome(s: str) -> int:
    from collections import Counter
    info = Counter(s)
    inpares = False
    maximo = 0
    
    print(info)
    for i, j in info.items():
        if j % 2 == 0:
            maximo += j
        else:
            inpares = True
            maximo += j - 1
    
    if inpares:
        maximo += 1
    
    return maximo
        
        
        
if __name__ == '__main__':
    s= ""
    result = longestPalindrome(s)
    print(result)